// import React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Checkbox from '@mui/material/Checkbox';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import React, { useRef, useEffect } from 'react';


const NoteList = ({ notes, setCurrentNote, isEditing, setIsEditing, updateNoteTitle }) => {
  const inputRef = useRef(null);
  useEffect(() => {
    if (isEditing !== null && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isEditing]);

  const handleBlur = (index) => {
    const newTitle = inputRef.current.value.trim();
    if (newTitle) {
      updateNoteTitle(index, newTitle);
    }
  };

  const handleKeyPress = (e, index) => {
    if (e.key === 'Enter') {
      handleBlur(index);
    }
  };


  return (
    <div className="notes-list">
      {/* <h2>笔记列表</h2> */}
      {/* <ul>
        {notes.map((note, index) => (
          <li key={index} onClick={() => setCurrentNote(index)}>
            {note.title}
          </li>
        ))}
      </ul> */}

      <list>
      {notes.map((note, index) => {
        const labelId = `note-${note.id}`;
        return (
          <ListItem>
              <ListItemButton role={undefined} dense>
                <ListItemIcon>
                  <Checkbox
                    edge="start"
                    checked={0}
                    tabIndex={-1}
                    disableRipple
                    inputProps={{ 'aria-labelledby': labelId }}
                  />
                </ListItemIcon>
                {isEditing === index ? (
                <input
                  type="text"
                  ref={inputRef}
                  defaultValue={note.title}
                  onBlur={() => handleBlur(index)}
                  onKeyPress={(e) => handleKeyPress(e, index)}
                />
              ) : (
                <ListItemText id={labelId} primary={note.title} />

              )}
            </ListItemButton>
          </ListItem>
        )
      } )}
      </list>
    </div>
  );
};

export default NoteList;
