import React, { useState, useEffect } from 'react';
import './App.css';
import NoteList from './components/NoteList';
import Button from '@mui/material/Button';
import NoteContent from './components/NoteContent'
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { v4 as uuidv4 } from 'uuid';

function App() {
  const [notes, setNotes] = useState(() => {
    const savedNotes = localStorage.getItem('notes');
    return savedNotes ? JSON.parse(savedNotes) : [];
  });
  const [currentNote, setCurrentNote] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [isEditing, setIsEditing] = useState(null);

  // const filteredNotes = notes.filter(note => note.title.toLowerCase().includes(searchQuery.toLowerCase()));

  useEffect(() => {
    localStorage.setItem('notes', JSON.stringify(notes));
  }, [notes]);

  const addNote = () => {
    const noteCount = notes.length + 1;
    const newNote = { id: uuidv4(), title: `笔记${noteCount}` };
    setNotes([newNote, ...notes]);
    setIsEditing(newNote.id); 
    console.log(notes);
  };

  const clearNote = () => {
    setNotes([]);
  }

  const updateNoteTitle = (id, title) => {
    const updatedNotes = notes.map((note) => (note.id === id ? { ...note, title } : note));
    setNotes(updatedNotes);
    setIsEditing(null); // 编辑完成后退出编辑状态
  };

  return (
    <Router>
          <div className="App">
      <div className="pageWrap">
