import logo from './logo.svg';
import React, { useState, useEffect } from 'react';
import './App.css';
import NoteList from './components/NoteList';
import Button from '@mui/material/Button';

function App() {
  const [notes, setNotes] = useState(() => {
    const savedNotes = localStorage.getItem('notes');
    return savedNotes ? JSON.parse(savedNotes) : [];
  });
  const [currentNote, setCurrentNote] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [isEditing, setIsEditing] = useState(null);

  // const filteredNotes = notes.filter(note => note.title.toLowerCase().includes(searchQuery.toLowerCase()));

  useEffect(() => {
    localStorage.setItem('notes', JSON.stringify(notes));
  }, [notes]);

  const addNote = (title) => {
    const newNote = {title};
    setNotes([...notes, newNote]);
    setIsEditing(0); 
  };

  const clearNote = () => {
    setNotes([]);
  }

  const updateNoteTitle = (index, title) => {
    const updatedNotes = notes.map((note, i) => (i === index ? { ...note, title } : note));
    setNotes(updatedNotes);
    setIsEditing(null); // 编辑完成后退出编辑状态
  };

  return (
    <div className="App">
      <div className="pageWrap">
        <div>
          <div>
            <h1>线上笔记管理应用</h1>
          </div>

          <div className='noteList'>
            <div>
              <h2>笔记列表</h2>
            </div>
            <div className="buttons">
              <div className="button"> <Button variant="outlined" onClick={() => addNote("")}>添加笔记</Button> </div>
              <div className="button"> <Button variant="outlined" onClick={() => clearNote()}>清空笔记</Button> </div>
            </div>

            <NoteList notes={notes} setCurrentNote={setCurrentNote} isEditing={isEditing} setIsEditing={setIsEditing} updateNoteTitle={updateNoteTitle}/>
          </div>
        </div>

      </div>
    </div>
  );
}

export default App;
