// import React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Checkbox from '@mui/material/Checkbox';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import React, { useRef, useEffect } from 'react';
// import Input from '@mui/material/Input';
// import Link from '@mui/material/Link';
import { Link } from 'react-router-dom';
import Button from '@mui/material/Button';
import NoteAddIcon from '@mui/icons-material/NoteAdd';
import Icon from '@mui/material/Icon';


const NoteList = (props) => {

  const { notes, isEditing, setIsEditing, updateNoteTitle, addNote, clearNote } = props;

  const inputRef = useRef(null);
  useEffect(() => {
    if (isEditing !== null && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isEditing]);

  const handleBlur = (id) => {
    const newTitle = inputRef.current.value.trim();
    updateNoteTitle(id, newTitle);

  };


  return (

    <div className="notes-list">
     <Button variant="outlined" onClick={() => clearNote()}>选择</Button> 
      <List>
      {notes.map((note, index) => {
        // const labelId = `note-${index}`;
        return (
          <ListItem>
              <ListItemButton role={undefined} dense>
                <ListItemIcon>
                  <Checkbox
                    edge="start"
                    checked={0}
                    tabIndex={-1}
                    disableRipple
                    inputProps={{ 'aria-labelledby': note.id }}
                  />
                </ListItemIcon>
                {isEditing === note.id ? (
                <input
                  type="text"
                  ref={inputRef}
                  defaultValue={note.title}
                  onBlur={() => handleBlur(note.id)}
                  className="noteNameInput"
                />
              )  : (
                <div>
                  <Link to={`/note/${note.id}`}>
                  <ListItemText id={note.id} primary={note.title} onDoubleClick={() => setIsEditing(note.id)}/>
                  </Link>
                </div>
              )}
            </ListItemButton>
          </ListItem>
        )
      } )}
      </List>
      <div className="addbuttonWrap"> 
        <Button         
        sx={{
          color: 'rgba(27, 42, 107, 0.7)',
          backgroundColor: 'white',
          borderRadius: '0',
          fontSize: '14px',
          fontWeight: '800',
          '&:hover': {
            backgroundColor: 'rgba(133, 168, 128, 0.1)',

          },
        }}
        className="addButton"
        startIcon={<NoteAddIcon />} 
        // variant="outlined" 
        onClick={() => addNote("")}>添加</Button> 
      </div>
    </div>
  );
};

export default NoteList;
