import React, { useState, useEffect } from 'react';
import './App.css';
import NoteList from './components/NoteList';
import NoteContent from './components/NoteContent'
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { v4 as uuidv4 } from 'uuid';

function App() {
  const [notes, setNotes] = useState(() => {
    const savedNotes = localStorage.getItem('notes');
    return savedNotes ? JSON.parse(savedNotes) : [];
  });
  const [currentNote, setCurrentNote] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [isEditing, setIsEditing] = useState(null);

  // const filteredNotes = notes.filter(note => note.title.toLowerCase().includes(searchQuery.toLowerCase()));

  useEffect(() => {
    localStorage.setItem('notes', JSON.stringify(notes));
  }, [notes]);

  const addNote = () => {
    const noteCount = notes.length + 1;
    const newNote = { 
      id: uuidv4(), 
      title: `笔记${noteCount}`, 
      content: `` };
    setNotes([newNote, ...notes]);
    setIsEditing(newNote.id); 
    console.log(notes);
  };

  const clearNote = () => {
    setNotes([]);
  }

  const updateNoteTitle = (id, title) => {
    const updatedNotes = notes.map((note) => (note.id === id ? { ...note, title } : note));
    setNotes(updatedNotes);
    setIsEditing(null); // 编辑完成后退出编辑状态
  };

  const updateNote = (id, updatedNote) => {
    const updatedNotes = notes.map(note => (note.id === id ? updatedNote : note));
    // updatedNotes.id = id;
    console.log("NOTE: " + updatedNotes.id);
    console.log("id"  + id);
    setNotes(updatedNotes);
  };

  return (
    <Router>
      <div className="App">
      <div className="pageWrap">

          <div className="caption">
            <div>
              我的笔记
            </div>
          </div>
          <div className='pageContent'>
            <div className="sidebar">
            <NoteList notes={notes} 
                      isEditing={isEditing} 
                      setIsEditing={setIsEditing} 
                      updateNoteTitle={updateNoteTitle}
                      addNote={addNote}
                      clearNote={clearNote}/>
            </div>

            <div className="noteDetail">
            <Routes>
              <Route path="/" element={<div>请选择一个笔记</div>} />
              <Route path="/note/:id" element={<NoteContent notes={notes} updateNote={updateNote}/>} />
            </Routes>
            </div>
          </div>

      </div>
    </div>
    </Router>

  );
}

export default App;
