import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const NoteContent = ({ notes, updateNote }) => {
    const { id } = useParams();

    const [note, setNote] = useState(null);

    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [ct, setCt] = useState('');
    const [ut, setUt] = useState('');

    const [isSaving, setIsSaving] = useState(false);

    const timeoutRef = useRef(null);


    useEffect(() => {
        const foundNote = notes.find(n => n.id === id);
        // console.log("Found id: " + foundNote.id);
        // console.log("Found:" + foundNote.content);
        if (foundNote) {
            setNote(foundNote);
            setTitle(foundNote.title);
            setContent(foundNote.content);
            setCt(foundNote.createTime);
            setUt(foundNote.updateTime);
            console.log("ct in add: " + ct)
            console.log("ut in add: " + ut)
        }
    }, [id, notes]);

    const saveNote = () => {
        const d = new Date();
        const timeoutId = setTimeout(() => {
            const updatedNote = {
                id,
                title,
                content,
                createTime: ct,
                updateTime: d.toLocaleDateString()
            };
            setUt(updatedNote.updateTime);
            updateNote(id, updatedNote);
            setIsSaving(false);
        }, 1000);
        return () => clearTimeout(timeoutId);
    };

    useEffect(() => {
        if (isSaving) {
            saveNote();
        }
    }, [isSaving, id, title, content]);

    // useEffect(() => {
    //     if (isSaving) {
    //         let d = new Date();
    //         const timeoutId = setTimeout(() => {
    //             console.log("d in update: " + d.toLocaleDateString());
    //             console.log("ct in update: " + ct);
    //             setUt(d.toLocaleDateString())
    //             updateNote(id, { id, title, content, ct, ut});
    //             setIsSaving(false);
    //             console.log(notes);
    //         }, 1000);
    //         return () => clearTimeout(timeoutId);
    //     }
    // }, [isSaving, id, title, content, updateNote]);


    const handleTitleChange = (e) => {
        setTitle(e.target.value);
        setNote({
            ...note,
            title: e.target.value,
          });
        setIsSaving(true);
    };

    const handleContentChange = (e) => {
        setContent(e.target.value);
        setNote({
            ...note,
            content: e.target.value,
          });
        setIsSaving(true);
    };



    // const handleSave = () => {
    //     updateNote(id, { ...note, title, content });
    //     navigate('/');
    // };

    return (
        <div className="note-content">
            <div className="noteInfo">
                {title} 
                <span className="timeInfo">创建时间：{ct}</span>
                <span className="timeInfo">修改时间：{ut}</span> 
            </div>
            <input
                type="text"
                value={title}
                // onChange={(e) => setTitle(e.target.value)}
                onChange={handleTitleChange}
                className="noteCaption"
            />


            <textarea
                value={content}
                // onChange={(e) => setContent(e.target.value)}
                onChange={handleContentChange}
                className="noteContent"
            />
        {/* <button onClick={handleSave}>保存</button>
        <button onClick={() => navigate('/')}>取消</button> */}
        </div>
    );
    };

export default NoteContent;
