// import React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Checkbox from '@mui/material/Checkbox';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import React, { useRef, useEffect } from 'react';
import Input from '@mui/material/Input';


const NoteList = ({ notes, setCurrentNote, isEditing, setIsEditing, updateNoteTitle }) => {
  const inputRef = useRef(null);
  useEffect(() => {
    if (isEditing !== null && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isEditing]);

  const handleBlur = (id) => {
    const newTitle = inputRef.current.value.trim();
    updateNoteTitle(id, newTitle);

  };

  const handleKeyPress = (e, id) => {
    if (e.key === 'Enter') {
      handleBlur(id);
    }
  };


  return (
    <div className="notes-list">

      <List>
      {notes.map((note, index) => {
        // const labelId = `note-${index}`;
        return (
          <ListItem>
              <ListItemButton role={undefined} dense>
                <ListItemIcon>
                  <Checkbox
                    edge="start"
                    checked={0}
                    tabIndex={-1}
                    disableRipple
                    inputProps={{ 'aria-labelledby': note.id }}
                  />
                </ListItemIcon>
                {isEditing === note.id ? (
                <input
                  type="text"
                  ref={inputRef}
                  defaultValue={note.title}
                  onBlur={() => handleBlur(note.id)}
                  onKeyPress={(e) => handleKeyPress(e, note.id)}
                  className=""
                />
              )  : (
                <ListItemText id={note.id} primary={note.title} onDoubleClick={() => setIsEditing(note.id)}/>
              )}
            </ListItemButton>
          </ListItem>
        )
      } )}
      </List>
    </div>
  );
};

export default NoteList;
